mapper.insert:
insert into fp_freeboard_article(
    user_id, title, content, read_count
) values(
    #{user_id}, #{title}, #{content}, 0
)

mapper.update:
update fp_freeboard_article 
set read_count = read_count + 1
where id = #{id}

update fp_freeboard_article
set 
    title = #{title},
    content = #{content}
where id = #{id}
